mapper.update:
update t_address set
    name=#{address.name}, province_name=#{address.provinceName}, province_code=#{address.provinceCode},
    city_name=#{address.cityName},city_code=#{address.cityCode}, area_name=#{address.areaName},
    area_code=#{address.areaCode}, zip=#{address.zip}, address=#{address.address}, phone=#{address.phone},
    tel=#{address.tel}, tag=#{address.tag}, modified_user=#{address.modifiedUser}, modified_time=#{address.modifiedTime}
where aid=#{aid}
