mapper.select:
select cid, uid, pid, image, title, c.price, c.num, p.price as realPrice
from t_cart c left join t_product p on p.id = c.pid
where uid = #{uid}

        and title like  #{title}

order by c.created_time desc

select cid, uid, pid, image, title, c.price, c.num, p.price as realPrice
from t_cart c left join t_product p on p.id = c.pid
where cid in (

        #{cid}

    )
order by c.created_time desc

mapper.delete:
delete from t_cart
where cid in (

        #{cid}

    )
